import './App.css';
import { useState } from "react";

function App() {
  const [count , setCount] = useState(0);

  const increase = () => { 
    setCount(count + 1); 
  }
  const decrease = () => {
    setCount(count - 1);
  }
  const settoZero = () => {
    setCount(0);
  }


  return( 
    <div className="App"> 
    <button onClick = {increase}>Increase</button>
    <button onClick = {decrease}>Decrease</button>
    <button onClick = {settoZero}>Set</button>
  </div>
  );
}
export default App;
