import './App.css';
import { useState } from "react";

function App() {
  const [count , setCount] = useState(0);

  const increase = () => { 
    setCount(count + 1); 
  }
  const decrease = () => {
    setCount(count - 1);
  }
  const setToZero = () => {
    setCount(0);
  }


  return( 
    <div className="App"> 
    <button onClick={increase}>Increase</button>

  </div>
  );
}

const User = (props) => {
  return (
    <div>
      <h1>{props.name}</h1>
      <h1>{props.age}</h1>
      <h1>{props.email}</h1>
    </div>
  )
}
export default App;
